<?phpnamespace config;use PDO;class Database extends PDO{  public $db = null;  public function __construct()  {    /*parent::__construct("mysql:host=localhost;dbname=mvc_db", 'root', '');    PDO::setAttribute(PDO::ATTR_EMULATE_PREPARES, false);    PDO::setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);*/    // Database Connection    $options = array(PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_OBJ, PDO::ATTR_ERRMODE => PDO::ERRMODE_WARNING);    parent::__construct("mysql:host=" . DB_HOST . ";dbname=" . DB_NAME . "", DB_USER, DB_PASS, $options);  }  public function select($sql, $data = array(), $fetchStyle = PDO::FETCH_ASSOC)  {    $stmt = $this->prepare($sql);    foreach ($data as $key => $value) {      $stmt->bindParam($key, $value);    }    $stmt->execute();    return $stmt->fetchAll($fetchStyle);  }}